  }



  render(){
    return (
      <div className="App">
        <Header></Header>
        <AddTask  ></AddTask>
        <ShowOptions></ShowOptions>
        <Activity taskName={this.state.newTasks.tasks[0].name} ></Activity>
        <Activity taskName={this.state.newTasks.tasks[1].name} ></Activity>
        <Activity taskName={this.state.newTasks.tasks[2].name} ></Activity>
        <Activity taskName={this.state.newTasks.tasks[3].name} ></Activity>
      </div>
    );
  }

}

export default App;
